//----userArr
let userArr = localStorage.getItem("userArr");
if (userArr === null) {
  localStorage.setItem("userArr", JSON.stringify([]));
  userArr = [];
} else userArr = JSON.parse(userArr);
//------------------------------------------------------
//-----------------------------
export { userArr };
