import classes from "./Review.module.css";
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { cartAction } from "../../store/index";
import { userArr } from "../../store/LocalStorage";
//--------------------------------------------------------
function Review({ product }) {
  //---hooks----
  const [quantity, setQuantity] = useState(1);
  const [error, setError] = useState(false);
  const dispatch = useDispatch();
  const login = useSelector((state) => state.login.login);
  let user = useSelector((state) => state.login.currentUser);
  let list = useSelector((state) => state.cart.list);
  let copyList = Object.assign([], list);
  //create price
  const price_string =
    product.price.slice(-product.price.length, -6) +
    "." +
    product.price.slice(-6, -3) +
    "." +
    product.price.slice(-3);
  //----------handle click button-----------
  // giới hạn số lượng từ 1-10
  const enterquantity = (e) => {
    if (e.target.value > 10) {
      setError(true);
      setQuantity(10);
      return;
    } else if (e.target.value < 1 && e.target.value !== "") {
      setError(true);
      setQuantity(1);
      return;
    }
    setQuantity(e.target.value);
    setError(false);
  };
  const decrease = () => {
    if (quantity === 1) {
      setError(true);
      return;
    }
    setQuantity((prev) => prev - 1);
    setError(false);
  };
  const increase = () => {
    if (quantity === 10) {
      setError(true);
      return;
    }
    setQuantity((prev) => Number(prev) + 1);
    setError(false);
  };
  //add prodcut
  const addHandle = () => {
    if (!login) {
      alert("Please log in to use this feature");
      return;
    }
    const productInfo = {
      name: product.name,
      price: product.price,
      image: product.img1,
      quantity: quantity,
    };
    dispatch(cartAction.ADD_CART([productInfo]));
    //-----------update localstorage------
    copyList.push(productInfo);
    const updateUser = {
      user: {
        ...user.user,
        cart: copyList,
      },
      index: user.index,
    };
    userArr[user.index] = updateUser.user;
    localStorage.setItem("currentUser", JSON.stringify(updateUser));
    localStorage.setItem("userArr", JSON.stringify(userArr));
    //---
    alert("Product added to cart");
  };
  //---------
  return (
    <div className={classes.review}>
      <p>{product.name}</p>
      <p>$ {product.price}</p>
      <p>{product.short_desc}</p>
      <p>
        CATEGORY: <span>{product.category}</span>
      </p>
      <div className={classes.quantity}>
        <p>QUANTITY</p>
        <button onClick={decrease}>
          <i class="fas fa-caret-left"></i>
        </button>
        <input type="number" value={quantity} onChange={enterquantity}></input>
        <button onClick={increase}>
          <i class="fas fa-caret-right"></i>
        </button>
        <button className={classes["add-button"]} onClick={addHandle}>
          Add to cart
        </button>
      </div>
      {error && <p>The number of products allowed is 1 to 10</p>}
    </div>
  );
}
//--------------------------------------------------------
export default Review;
