import classes from "./ProductList.module.css";
//-------------------------------------------
function ProductList() {
  //---------hooks----

  //--------button handle-------------
  const DeleteHandle = (index) => {};
  //---------------------------------
  return list.length === 0 ? (
    <p>No Products</p>
  ) : (
    <>
      {list.map((cur, i) => (
        <div className={classes.product} key={i}>
          <img src={cur.image} alt="product" />
          <p className={classes.name}>{cur.name}</p>
          <p className={classes.price}>{convert(cur.price)} VND</p>
          <div className={classes.quantity}>
            <button onClick={() => decrease(i)}>
              <i class="fas fa-caret-left"></i>
            </button>
            <p>{cur.quantity}</p>
            <button onClick={() => increase(i)}>
              <i class="fas fa-caret-right"></i>
            </button>
          </div>
          <p className={classes.price}>
            {convert(String(cur.price * cur.quantity))} VND
          </p>
          <button
            onClick={() => {
              DeleteHandle(i);
            }}
          >
            <i class="fas fa-trash-alt"></i>
          </button>
        </div>
      ))}
    </>
  );
}
//-----------------------------------
export default ProductList;
//----------convert number to string-------
export function convert(number) {
  let i = number.length;
  let counter = 0;
  let price = "";
  while (i--) {
    price = number.charAt(i) + price;
    counter++;
    if (counter === 3 && i !== 0) {
      price = "." + price;
      counter = 0;
    }
  }
  return price;
}
