import classes from "./ProductList.module.css";
import { json, useLoaderData } from "react-router-dom";
import ProductItem from "./ProductItem";
//-----------------------------------------------------------
function ProductList() {
  const data = useLoaderData();
  return (
    <div>
      <div className={classes.search}>
        <input
          placeholder="Enter search here!"
          className={classes.input}
        ></input>
        <select className={classes.option} defaultValue="default">
          <option value="default">Default Sorting</option>
        </select>
      </div>
      <div className={classes.productlist}>
        {data.map((cur, i) => (
          <ProductItem product={cur} key={i}></ProductItem>
        ))}
      </div>
      <div className={classes.results}>
        <div className={classes.change}>
          <button>
            <i class="fas fa-angle-double-left"></i>
          </button>
          {page && <span className={classes.page}>{page}</span>}
          <button>
            <i class="fas fa-angle-double-right"></i>
          </button>
        </div>
      </div>
    </div>
  );
}
//-----------------------------------------------------------
export default ProductList;
export async function loader({ request }) {
  const searchParams = new URL(request.url).searchParams;
  const mode = searchParams.get("mode");
  const response = await fetch(
    "https://firebasestorage.googleapis.com/v0/b/funix-subtitle.appspot.com/o/Boutique_products.json?alt=media&token=dc67a5ea-e3e0-479e-9eaf-5e01bcd09c74"
  );
  if (!response.ok) {
    throw json(
      { message: "Can not fecth detail event" },
      {
        status: 550,
      }
    );
  }
  const data = await response.json();
  if (mode === "all") {
    return data;
  }
  const data_filter = data.filter((cur) => cur.category === mode);
  return data_filter;
}
