import classes from "./LoginForm.module.css";
import { Link, useSearchParams, useNavigate } from "react-router-dom";
import { useRef } from "react";
//---------------------------------------------
function LoginForm() {
  //----hooks----
  const navigate = useNavigate();
  const [params, setParams] = useSearchParams();
  const login = params.get("mode") === "login" ? true : false;
  const form = useRef();
  //---click login handle-------------------
  const LoginHandle = () => {
    navigate("/login?mode=login");
  };
  //---click login handle-------------------
  const SignupHandle = () => {
    e.preventDefault();
    const formData = new FormData(form.current);
    const data = {
      userName: formData.get("userName"),
      password: formData.get("password"),
    };
    for (let key in data) {
      if (!data[key]) {
        alert(`${key} empty`);
        return;
      }
    }

    fetch("http://localhost:5000/login", {
      method: "POST",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((result) => result.json())
      .then((data) => {
        if (!data.success) {
          alert(data.inform);
        } else {
          localStorage.setItem("login", data._id);
          navigate("/");
        }
      })
      .catch((err) => console.log(err));
    navigate("/login?mode=login");
  };
  //----click logout handle----
  const LogoutHandle = () => {};
  //---return------
  return false ? (
    <div>
      <button className={classes["button-logout"]} onClick={LogoutHandle}>
        <i class="fas fa-sign-out-alt"></i> Logout
      </button>
    </div>
  ) : (
    <div className={classes.container}>
      <form className={classes.form} ref={form}>
        <h2>{login ? "Sign In" : "Sign Up"}</h2>
        <div>
          {!login && <input placeholder="Full Name" name="fullname"></input>}
          <input placeholder="Email" name="email"></input>
          <input placeholder="Password" name="password"></input>
          {!login && <input placeholder="Phone" name="phoneNumber"></input>}
          {login && <button onClick={LoginHandle}>SIGN IN</button>}
          {!login && <button onClick={SignupHandle}>SIGN UP</button>}
        </div>
        <p>
          {login ? "Create an account? " : "Login? "}
          <Link
            className={classes.link}
            to={`/login?mode=${login ? "signup" : "login"}`}
          >
            {login ? "Sign Up" : "Click"}
          </Link>
        </p>
      </form>
    </div>
  );
}
//---------------------------------------------
export default LoginForm;
