import classes from "./ProductItem.module.css";
import { useDispatch } from "react-redux";
import { popupAction } from "../../store/index";
//---------------------------------------------------------
function ProductItem({ product }) {
  //use hooks
  const dispatch = useDispatch();
  //button handle
  const showHandle = () => {
    dispatch(popupAction.SHOW_POPUP(product));
  };
  //create price
  const price_string =
    product.price.slice(-product.price.length, -6) +
    "." +
    product.price.slice(-6, -3) +
    "." +
    product.price.slice(-3);
  //------------
  return (
    <button className={classes.product} onClick={showHandle}>
      <img src={product.img1} alt={product.name} className={classes.image} />
      <p>{product.name}</p>
      <p>{price_string} VND</p>
    </button>
  );
}
export default ProductItem;
