import classes from "./CheckOutPage.module.css";
import Banner from "./Banner";
import { json, useLoaderData } from "react-router-dom";
import { convert } from "../CartPage/ProductList";
//---------------------------------------------
function CheckOutPage() {
  const data = useLoaderData();
  const list = data.list;
  const total = list.reduce(
    (total, cur) => total + cur.product_id.price * cur.quantity,
    0
  );
  return (
    <div className={classes.container}>
      <Banner></Banner>
      <h2 className={classes.heading}>BILLING DETAILS</h2>
      <div className={classes.div_flex}>
        <form>
          <label htmlFor="name">FULL NAME:</label>
          <input id="name" placeholder="Enter Your Full Name Here!"></input>
          <label htmlFor="email">EMAIL:</label>
          <input id="email" placeholder="Enter Your Email Here!"></input>
          <label htmlFor="phone">PHONE NUMBER:</label>
          <input id="phone" placeholder="Enter Your Phone Number Here!"></input>
          <label htmlFor="address">ADDRESS:</label>
          <input id="address" placeholder="Enter Your Address Here!"></input>
          <button type="button">Place order</button>
        </form>
        <div className={classes.order}>
          <h2>YOUR ORDER</h2>
          {list.map((cur, i) => (
            <div>
              <p>{cur.name}</p>
              <p>
                {convert(cur.product_id.price)} $ x {cur.quantity}
              </p>
            </div>
          ))}
          <div className={classes.total}>
            <p>TOTAL</p>
            <p>{convert(String(total))} VND</p>
          </div>
        </div>
      </div>
    </div>
  );
}
//--------------------------------------------
export default CheckOutPage;
