import classes from "./Product.module.css";
import { json, useLoaderData } from "react-router-dom";
import Review from "./Review";
import Image from "./Image";
import Description from "./Description";
import Related from "./Related";
//------------------------------------------------------
function Product() {
  const data = useLoaderData();
  return (
    <div className={classes.product}>
      <div className={classes["product-review"]}>
        <Image product={data.product}></Image>
        <Review product={data.product}></Review>
      </div>
      <Description desc={data.product.long_desc}></Description>
      <Related data={data.related_product}></Related>
    </div>
  );
}
//------------------------------------------------------
export default Product;
export async function loader({ params }) {
  const response = await fetch("http://localhost:5000/get-detail", {
    method: "POST",
    headers: {
      "Content-type": "application/json",
    },
    body: JSON.stringify(data),
  });
  if (!response.ok) {
    throw json(
      { message: "Can not fecth detail event" },
      {
        status: 550,
      }
    );
  }
  const data = await response.json();
  const product = data.filter((cur) => cur._id.$oid === params.productID);
  const category = data.filter(
    (cur) =>
      cur.category === product[0].category && cur._id.$oid !== params.productID
  );

  const data_filter = {
    product: product[0],
    related_product: category,
  };
  return data_filter;
}
