import classes from "./LoginForm.module.css";
import { Link, useSearchParams, useNavigate } from "react-router-dom";
import { useRef, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { loginAction, cartAction } from "../../store/index";
import { userArr } from "../../store/LocalStorage";
//---------------------------------------------
function LoginForm() {
  //----hooks----
  const [type, setType] = useState("password");
  const [notify, setNotify] = useState("");
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const data = useSelector((state) => state.login.login);
  //---get params--
  const [params, setParams] = useSearchParams();
  const login = params.get("mode") === "login" ? true : false;
  //---useRef----
  const email = useRef();
  const password = useRef();
  const name = useRef();
  const phone = useRef();
  //---click login handle-------------------
  const LoginHandle = () => {
    //check input !== ""
    if (email.current.value.trim() === "") {
      setNotify("Please fill in Email");
      email.current.focus();
      return;
    }
    if (password.current.value.trim() === "") {
      setNotify("Please fill in Password");
      password.current.focus();
      return;
    }
    if (!login && name.current.value.trim() === "") {
      setNotify("Please fill in Full Name");
      name.current.focus();
      return;
    }
    if (!login && phone.current.value.trim() === "") {
      setNotify("Please fill in Phone");
      phone.current.focus();
      return;
    }
    setNotify("");
    //---validate user---
    const user = validate(email.current.value.trim()); //trả về thông tin user nếu tồn tại và vị trí của user trong array
    //-sign up--
    if (!login) {
      if (user.user) {
        setNotify("User already exists");
        email.current.focus();
        return;
      }
      if (password.current.value.trim().length < 8) {
        setNotify("Password must be more than 8 characters");
        password.current.focus();
        return;
      }
      const userInput = {
        email: email.current.value,
        password: password.current.value,
        name: name.current.value,
        phone: phone.current.value,
        cart: [],
      };
      userArr.push(userInput);
      localStorage.setItem("userArr", JSON.stringify(userArr));
      alert("Sign Up Success!!");
      password.current.value = "";
      navigate("/login?mode=login");
    }
    //--sign in--
    else {
      if (!user.user) {
        setNotify("User does not exist");
        email.current.focus();
        return;
      }
      if (user.user.password === password.current.value) {
        localStorage.setItem("currentUser", JSON.stringify(user));
        dispatch(loginAction.ON_LOGIN(user));
        dispatch(cartAction.ADD_CART(user.user.cart));
      } else {
        setNotify("Incorrect password");
        password.current.value = "";
        password.current.focus();
      }
    }
  };
  //----click logout handle----
  const LogoutHandle = () => {
    dispatch(loginAction.ON_LOGOUT());
    //----khoi tao lai gia tri list cua redux
    dispatch(cartAction.RESET_LIST());
    localStorage.removeItem("currentUser");
  };
  //---return------
  return data ? (
    <div>
      <button className={classes["button-logout"]} onClick={LogoutHandle}>
        <i class="fas fa-sign-out-alt"></i> Logout
      </button>
    </div>
  ) : (
    <div className={classes.container}>
      <div className={classes.form}>
        <h2>{login ? "Sign In" : "Sign Up"}</h2>
        <div>
          {!login && <input placeholder="Full Name" ref={name}></input>}
          <input placeholder="Email" ref={email}></input>
          <input
            placeholder="Password"
            type={type}
            ref={password}
            onFocus={() => setType("text")}
            onBlur={() => setType("password")}
          ></input>
          {!login && <input placeholder="Phone" ref={phone}></input>}
          <p>{notify}</p>
          <button onClick={LoginHandle}>{login ? "SIGN IN" : "SIGN UP"}</button>
        </div>
        <p>
          {login ? "Create an account? " : "Login? "}
          <Link
            className={classes.link}
            to={`/login?mode=${login ? "signup" : "login"}`}
            onClick={() => setNotify("")}
          >
            {login ? "Sign Up" : "Click"}
          </Link>
        </p>
      </div>
    </div>
  );
}
//---------------------------------------------
export default LoginForm;
//------------------------validate funtion--------------------------
function validate(email) {
  console.log(userArr);
  let index, user;
  for (let i in userArr) {
    if (userArr[i].email === email) {
      index = i;
      user = userArr[i];
      break;
    }
  }
  return { user, index };
}
