import classes from "./LoginForm.module.css";
import { Link, useSearchParams, useNavigate } from "react-router-dom";
import { useRef, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { loginAction, cartAction } from "../../store/index";
import { userArr } from "../../store/LocalStorage";
//---------------------------------------------
function LoginForm() {
  //----hooks----
  const navigate = useNavigate();
  //---get params--
  const [params, setParams] = useSearchParams();
  const login = params.get("mode") === "login" ? true : false;
  //---click login handle-------------------
  const LoginHandle = () => {
    navigate("/login?mode=login");
  };
  //----click logout handle----
  const LogoutHandle = () => {
    dispatch(loginAction.ON_LOGOUT());
    //----khoi tao lai gia tri list cua redux
    dispatch(cartAction.RESET_LIST());
    localStorage.removeItem("currentUser");
  };
  //---return------
  return data ? (
    <div>
      <button className={classes["button-logout"]} onClick={LogoutHandle}>
        <i class="fas fa-sign-out-alt"></i> Logout
      </button>
    </div>
  ) : (
    <div className={classes.container}>
      <div className={classes.form}>
        <h2>{login ? "Sign In" : "Sign Up"}</h2>
        <div>
          {!login && <input placeholder="Full Name" ref={name}></input>}
          <input placeholder="Email" ref={email}></input>
          <input
            placeholder="Password"
            type={type}
            ref={password}
            onFocus={() => setType("text")}
            onBlur={() => setType("password")}
          ></input>
          {!login && <input placeholder="Phone" ref={phone}></input>}
          <p>{notify}</p>
          <button onClick={LoginHandle}>{login ? "SIGN IN" : "SIGN UP"}</button>
        </div>
        <p>
          {login ? "Create an account? " : "Login? "}
          <Link
            className={classes.link}
            to={`/login?mode=${login ? "signup" : "login"}`}
            onClick={() => setNotify("")}
          >
            {login ? "Sign Up" : "Click"}
          </Link>
        </p>
      </div>
    </div>
  );
}
//---------------------------------------------
export default LoginForm;
//------------------------validate funtion--------------------------
function validate(email) {
  console.log(userArr);
  let index, user;
  for (let i in userArr) {
    if (userArr[i].email === email) {
      index = i;
      user = userArr[i];
      break;
    }
  }
  return { user, index };
}
