import classes from "./ProductList.module.css";
import { useSelector, useDispatch } from "react-redux";
import { cartAction } from "../../store/index";
import { userArr } from "../../store/LocalStorage";
//-------------------------------------------
function ProductList() {
  //---------hooks----
  const list = useSelector((state) => state.cart.list);
  let user = useSelector((state) => state.login.currentUser);
  const dispatch = useDispatch();
  //--------update localstorage when reload------------
  let updateUser;
  if (user.user) {
    updateUser = {
      user: {
        ...user.user,
        cart: list,
      },
      index: user.index,
    };
    userArr[user.index] = updateUser.user;
    localStorage.setItem("currentUser", JSON.stringify(updateUser));
    localStorage.setItem("userArr", JSON.stringify(userArr));
  }
  //--------button handle-------------
  const DeleteHandle = (index) => {
    if (
      window.confirm("You want to remove the product from the shopping cart?")
    ) {
      dispatch(cartAction.DELETE_CART(index));
    }
  };
  const decrease = (index) => {
    dispatch(cartAction.UPDATE_CART({ index: index, type: "decrease" }));
  };
  const increase = (index) => {
    dispatch(cartAction.UPDATE_CART({ index: index, type: "increase" }));
  };
  //---------------------------------
  return list.length === 0 ? (
    <p>No Products</p>
  ) : (
    <>
      {list.map((cur, i) => (
        <div className={classes.product} key={i}>
          <img src={cur.image} alt="product" />
          <p className={classes.name}>{cur.name}</p>
          <p className={classes.price}>{convert(cur.price)} VND</p>
          <div className={classes.quantity}>
            <button onClick={() => decrease(i)}>
              <i class="fas fa-caret-left"></i>
            </button>
            <p>{cur.quantity}</p>
            <button onClick={() => increase(i)}>
              <i class="fas fa-caret-right"></i>
            </button>
          </div>
          <p className={classes.price}>
            {convert(String(cur.price * cur.quantity))} VND
          </p>
          <button
            onClick={() => {
              DeleteHandle(i);
            }}
          >
            <i class="fas fa-trash-alt"></i>
          </button>
        </div>
      ))}
    </>
  );
}
//-----------------------------------
export default ProductList;
//----------convert number to string-------
export function convert(number) {
  let i = number.length;
  let counter = 0;
  let price = "";
  while (i--) {
    price = number.charAt(i) + price;
    counter++;
    if (counter === 3 && i !== 0) {
      price = "." + price;
      counter = 0;
    }
  }
  return price;
}
