import { NavLink } from "react-router-dom";
import classes from "./NavBar.module.css";
import { useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { loginAction, cartAction } from "../../store/index";
//----------------pages-----------------------
function Navigation() {
  //---hooks---------
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const login = useSelector((state) => state.login.login);
  let user = useSelector((state) => state.login.currentUser);
  user = user.name;
  let name;
  if (user) {
    name = user.name;
    if (name.length >= 7) name = name.slice(0, 7);
  }
  ///---logout---
  const LogoutHandle = () => {
    dispatch(loginAction.ON_LOGOUT());
    //----khoi tao lai gia tri list cua redux
    dispatch(cartAction.RESET_LIST());
    localStorage.removeItem("currentUser");
    navigate("/login?mode=login");
  };
  //--------------
  return (
    <div className={classes.navbar}>
      <div>
        <NavLink
          to="/"
          className={({ isActive }) =>
            isActive ? classes.active : classes.link
          }
        >
          Home
        </NavLink>
        <NavLink
          to="/shop?mode=all"
          className={({ isActive }) =>
            isActive ? classes.active : classes.link
          }
        >
          Shop
        </NavLink>
      </div>
      <h1 className={classes.heading}>BOUTIQUE</h1>
      <div>
        <NavLink
          to="/cart"
          className={({ isActive }) =>
            isActive ? classes.active : classes.link
          }
        >
          <i class="fas fa-shopping-cart"></i> Cart
        </NavLink>
        <NavLink
          to="/login?mode=login"
          className={({ isActive }) =>
            isActive ? classes.active : classes.link
          }
        >
          <i className="fas fa-user"></i> {login ? name : "Login"}
        </NavLink>
        {login && (
          <button onClick={LogoutHandle} className={classes["button-logout"]}>
            (Logout)
          </button>
        )}
      </div>
    </div>
  );
}
//------------------export------------------------
export default Navigation;
